function PlayerReports() {
  const [playerName, setPlayerName] = useState('');
  const [playerRole, setPlayerRole] = useState('');
  const [selectedPlayer, setSelectedPlayer] = useState('');
  const [wordCount, setWordCount] = useState('');
  const [summary, setSummary] = useState('');
  const [playerImage, setPlayerImage] = useState('');

  const handlePlayerNameChange = (event) => {
    setPlayerName(event.target.value);
  };

  const handlePlayerRoleChange = (event) => {
    setPlayerRole(event.target.value);
  };

  const handlePlayerSelection = (event) => {
    const selected = event.target.value;
    setSelectedPlayer(selected);
    const player = players.find(p => p.name === selected);
    if (player) {
      setPlayerImage(player.image);
    }
  };

  const handleWordCountChange = (event) => {
    setWordCount(event.target.value);
  };

  const handleGenerateSummary = () => {
    setSummary(`Generating a summary for ${selectedPlayer} with ${wordCount} words...`);
    // Call to backend service or API to generate summary can be placed here.
    // For this example, we're setting a static summary.
    setSummary(`Summary for ${selectedPlayer} (${playerRole}): This player is known for their exceptional skills and has contributed significantly to their team's success. They have a strong track record and are highly regarded in the cricket community.`);
  };

  return (
    <div className="player-reports-container ">
      <header className="form-container">
        <br/>
        <h1>Player Reports</h1>
      </header>
      <div className="form-group">
        <label>
          Player Name:
          <input type="text" value={playerName} onChange={handlePlayerNameChange} />
        </label>
        <label>
          Player Role:
          <input type="text" value={playerRole} onChange={handlePlayerRoleChange} />
        </label>
        <label>
          Select Player:
          <select value={selectedPlayer} onChange={handlePlayerSelection}>
            <option value="">--Choose a player--</option>
            {players.map(player => (
              <option key={player.name} value={player.name}>{player.name}</option>
            ))}
          </select>
        </label>
        {playerImage && <img src={playerImage} alt={selectedPlayer} style={{ width: '100px', height: '100px' }} />}
      </div>
      <div className="Summary-section">
        <label>
          Word Count:
          <input type="number" value={wordCount} onChange={handleWordCountChange} />
        </label>
        <button onClick={handleGenerateSummary}>Generate Summary</button>
        {summary && <p>{summary}</p>}
      </div>
    </div>
  );
}
  
export default PlayerReports;
