import React, { useState } from 'react';
import './PlayerReports.css';

const PlayerReports = () => {
  const [playerName, setPlayerName] = useState('');
  const [playerRole, setPlayerRole] = useState('');
  const [playerPhotoUrl, setPlayerPhotoUrl] = useState('');
  const [summaryLength, setSummaryLength] = useState(100); // Added state for summary length

  const playerPhotos = {
    "ViratKohli": "/images/ViratKohli.jpg",
    "Virat": "/images/ViratKohli.jpg",
    "Kohli": "/images/ViratKohli.jpg",
    "Kohli": "/images/ViratKohli.jpg",
    "Kohli": "/images/ViratKohli.jpg",
    "Kohli": "/images/ViratKohli.jpg",
    "Kohli": "/images/ViratKohli.jpg",
    "Kohli": "/images/ViratKohli.jpg",
    "Kohli": "/images/ViratKohli.jpg",
    "Kohli": "/images/ViratKohli.jpg",
    "Kohli": "/images/ViratKohli.jpg",
     "Kohli": "/images/ViratKohli.jpg",
    
    // Add more player names and their photo paths here
  };

  const handleGenerate = () => {
    const photoUrl = playerPhotos[playerName] || 'https://via.placeholder.com/150';
    setPlayerPhotoUrl(photoUrl);
    console.log({ playerName, playerRole, summaryLength, photoUrl });
  };

  return (
    <div className="player-reports-container">
      <div className="form-container">
        <h2>Player Report</h2>
        <div className="form-group">
          <label htmlFor="playerName">Enter Name of the Player:</label>
          <input
            type="text"
            id="playerName"
            value={playerName}
            onChange={(e) => setPlayerName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="playerRole">Enter Role of the Player:</label>
          <input
            type="text"
            id="playerRole"
            value={playerRole}
            onChange={(e) => setPlayerRole(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="summaryLength">Summary Length (words):</label>
          <input
            type="number"
            id="summaryLength"
            value={summaryLength}
            onChange={(e) => setSummaryLength(e.target.value)}
          />
        </div>
        <button onClick={handleGenerate}>Generate</button>
      </div>
      <div className="summary-display1">
        {playerPhotoUrl && (
          <div className="player-photo">
            <img src={playerPhotoUrl} alt="Player" />
          </div>
        )}
      </div>
    </div>
  );
};

export default PlayerReports;
