import React, { useState } from 'react';
import './MatchSummary.css';
import axios from 'axios';

const MatchSummary = () => {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState('');

  const handleQueryChange = (e) => {
    setQuery(e.target.value);
    
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const result = await axios.post('https://a7e9-34-143-173-149.ngrok-free.app/query', { query });
      setResponse(result.data.answer);
    } catch (error) {
      console.error('Error querying the backend:', error);
      setResponse('Error querying the backend. Please check the console for more details.');
    }
  };


  return (
    <form onSubmit={handleSubmit}>
    <div className="match-summary-container">
      <div className="form-container">
        <br/>
        <h2>ASK HERE</h2>
        <div className="form-group">
          <label htmlFor="teamA">Enter the IPL match to generate match summary</label>
          <input
            type="text"
            id="query"
            value={query}
            onChange={handleQueryChange}
          />
        </div>
       
        {/* <div className="form-group">
          <label htmlFor="summaryLength">Summary Length (words):</label>
          <input
            type="number"
            id="summaryLength"
            value={summaryLength}
            onChange={(e) => setSummaryLength(e.target.value)}
          />
        </div> */}
        <button type="submit">Submit</button>
      </div>
      {response && (
        <div className='summary-display'>
          <h3>Response:</h3>
          <p>{response}</p>
        </div>
      )}        {/* This is where the generated summary would be displayed */}
      </div>
      </form>
  );
};

export default MatchSummary;
