import React, { useState } from 'react';
import './MatchSummary.css';

const MatchSummary = () => {
  const [teamA, setTeamA] = useState('');
  const [teamB, setTeamB] = useState('');
  const [scoreA, setScoreA] = useState('');
  const [scoreB, setScoreB] = useState('');
  const [summaryLength, setSummaryLength] = useState(100);

  const handleGenerate = () => {
    // Handle the generation logic
    // You would typically make a backend request here with the match details and summary length
    console.log({ teamA, teamB, scoreA, scoreB, summaryLength });
  };

  return (
    <div className="match-summary-container">
      <div className="form-container">
        <br/>
        <h2>ASK HERE</h2>
        <div className="form-group">
          <label htmlFor="teamA">Enter the IPL match to generate match summary</label>
          <input
            type="text"
            id="teamA"
            value={teamA}
            onChange={(e) => setTeamA(e.target.value)}
          />
        </div>
       
        <div className="form-group">
          <label htmlFor="summaryLength">Summary Length (words):</label>
          <input
            type="number"
            id="summaryLength"
            value={summaryLength}
            onChange={(e) => setSummaryLength(e.target.value)}
          />
        </div>
        <button onClick={handleGenerate}>Generate</button>
      </div>
      <div className="summary-display">
        {/* This is where the generated summary would be displayed */}
      </div>
    </div>
  );
};

export default MatchSummary;
