import React, { useState } from 'react';
import './PlayerReports.css';

const PlayerReports = () => {
  const [playerName, setPlayerName] = useState('');
  const [playerRole, setPlayerRole] = useState('');
  const [playerPhotoUrl, setPlayerPhotoUrl] = useState('');
  const [summaryLength, setSummaryLength] = useState(100);
  const [filteredPlayers, setFilteredPlayers] = useState([]); // Added state for summary length

  const playerPhotos = {
    "Virat Kohli": "/images/Virat Kohli.jpeg",
    "Virat": "/images/Virat Kohli.jpeg",
    "Kohli": "/images/Virat Kohli.jpeg",
    

    // Add more player names and their photo paths here
  };
  const handleNameChange = (event) => {
    const selectedName = event.target.value;
    setPlayerName(selectedName);
    setPlayerPhotoUrl(playerPhotos[selectedName] || '');

    if (selectedName) {
      const filtered = Object.keys(playerPhotos).filter((name) =>
        name.toLowerCase().startsWith(selectedName.toLowerCase())
      );
      setFilteredPlayers(filtered);
    } else {
      setFilteredPlayers([]);
    }
  };

  const handleRoleChange = (event) => {
    setPlayerRole(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
  }

  const handleGenerate = () => {
    const photoUrl = playerPhotos[playerName] || 'https://via.placeholder.com/150';
    setPlayerPhotoUrl(photoUrl);
    console.log({ playerName, playerRole, summaryLength, photoUrl });
  };

  return (
    <div className="player-reports-container">
      <div className="form-container">
        <h2>Player Report</h2>
        <div className="form-group">
          <form onSubmit={handleSubmit}>
            <label>
              Player Name:
              <input
                type="text"
                value={playerName}
                onChange={handleNameChange}
                list="playerNameSuggestions"
              />
              <datalist id="playerNameSuggestions">
                {filteredPlayers.map((name) => (
                  <option key={name} value={name} />
                ))}
              </datalist>
            </label>
          </form>
        </div>
        <div className="form-group">
          <label htmlFor="playerRole">Enter Role of the Player:</label>
          <input
            type="text"
            id="playerRole"
            value={playerRole}
            onChange={handleRoleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="summaryLength">Summary Length (words):</label>
          <input
            type="number"
            id="summaryLength"
            value={summaryLength}
            onChange={(e) => setSummaryLength(e.target.value)}
          />
        </div>
        <button onClick={handleGenerate}>Generate</button>
      </div>
      <div className="summary-display1">
        {playerPhotoUrl && (
          <div className="player-photo">
            <img src={playerPhotoUrl} alt="Player" />
          </div>
        )}
      </div>
    </div>
  );
};

export default PlayerReports;
