  };

  return (
    <div className="match-summary-container">
      <div className="form-container">
        <br/>
        <h2>PLAYER PERFORMANCE</h2>
        <div className="form-group">
          <label htmlFor="teamA">Enter the name of the player</label>
          <input
            type="text"
            id="teamA"
            value={teamA}
            onChange={(e) => setTeamA(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="teamA">Enter the  Player Role</label>
          <input
            type="text"
            id="teamA"
            value={teamA}
            onChange={(e) => setTeamA(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="summaryLength">Summary Length (words):</label>
          <input
            type="number"
            id="summaryLength"
            value={summaryLength}
            onChange={(e) => setSummaryLength(e.target.value)}
          />
        </div>
        <button onClick={handleGenerate}>Generate</button>
      </div>
      <div className="summary-display">
        {/* This is where the generated summary would be displayed */}
      </div>
    </div>
  );
};

export default PlayerReports;
