import React, { useState } from 'react';
import './PlayerReports.css';

const PlayerReports = () => {
  const [playerName, setPlayerName] = useState('');
  const [playerRole, setPlayerRole] = useState('');
  const [summaryLength, setSummaryLength] = useState(100);
  const [playerPhoto, setPlayerPhoto] = useState('');

  // Add your player photo paths here
  const playerImages = {
    "Virat": "/images/ViratKohli.jpg",
   
  };

  const handleGenerate = () => {
    // Handle the generation logic
    // You would typically make a backend request here with the match details and summary length
    console.log({ playerName, playerRole, summaryLength });

    // Set the player photo based on the player name
    if (playerImages[playerName]) {
      setPlayerPhoto(playerImages[playerName]);
    } else {
      setPlayerPhoto('path/to/default-image.jpg'); // Default image if player not found
    }
  };

  return (
    <div className="player-reports-container">
      <div className="form-container">
        <h2>PLAYER PERFORMANCE</h2>
        <div className="form-group">
          <label htmlFor="playerName">Enter the name of the player</label>
          <input
            type="text"
            id="playerName"
            value={playerName}
            onChange={(e) => setPlayerName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="playerRole">Enter the Player Role</label>
          <input
            type="text"
            id="playerRole"
            value={playerRole}
            onChange={(e) => setPlayerRole(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="summaryLength">Summary Length (words):</label>
          <input
            type="number"
            id="summaryLength"
            value={summaryLength}
            onChange={(e) => setSummaryLength(e.target.value)}
          />
        </div>
        <button onClick={handleGenerate}>Generate</button>
      </div>
      <div className="summary-display">
        {/* This is where the generated summary would be displayed */}
      </div>
      <div className="player-photo">
        {playerPhoto && <img src={playerPhoto} alt={playerName} />}
      </div>
    </div>
  );
};

export default PlayerReports;
